function kecamatan(str){
	// alert(str);
	if (str==""){
	  document.getElementById("nongolkelurahan").innerHTML="";
	  return;
	}
	if (window.XMLHttpRequest){
		xmlhttp=new XMLHttpRequest();
	}else{
		xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
	}
	xmlhttp.onreadystatechange=function(){
		if (xmlhttp.readyState==4 && xmlhttp.status==200){
			document.getElementById("nongolkelurahan").innerHTML=xmlhttp.responseText;
		}
	}
	xmlhttp.open("GET","tampilinkelurahan.php?q="+str,true);
	xmlhttp.send();
} 

function cekuang(){
	var hargatanah = eval($('#hargatanah').val());			
	if (hargatanah==""){
	  document.getElementById("crot").innerHTML="";
	  return;
	}
	if (window.XMLHttpRequest){
		xmlhttp=new XMLHttpRequest();
	}else{
		xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
	}
	xmlhttp.onreadystatechange=function(){
		if (xmlhttp.readyState==4 && xmlhttp.status==200){
			document.getElementById("crot").innerHTML=xmlhttp.responseText;
		}
	}
	xmlhttp.open("GET","encrot.php?nominal="+hargatanah,true);
	xmlhttp.send();
}

